# EnterPostInstall.ps1
# Initializes the per-user PostInstall state machine after Active Setup scripts finish.

. "C:\MySetup\Scripts\Apply-Registry.ps1"

$RegPath = "HKCU:\Software\MySetup\PostInstall"

function Get-SetupCycle {
    $SetupCycle = (Get-ItemProperty -Path $RegPath -Name SetupCycle -ErrorAction SilentlyContinue).SetupCycle
    if (-not $SetupCycle) {
        $SetupCycle = 1
    }
    return @{
        Path = "$RegPath"
        Name = "SetupCycle"
        Type = "Dword"
        Value = $SetupCycle
        Description = "Determine the next SetupCycle value"
    }
}

function Get-LastBootTime {
    $BootTime = (Get-CimInstance Win32_OperatingSystem).LastBootUpTime
    return @{
        Path = "$RegPath"
        Name = "LastBootTime"
        Type = "QWord"
        Value = ([long]$BootTime.ToFileTimeUtc())
        Description = "Record the current boot time"
    }
}

$entries = @(
    Get-SetupCycle
    @{
        Path = "$RegPath"
        Name = "SetupComplete"
        Type = "DWord"
        Value = 1
        Description = "Mark setup as complete"
    },
    @{
        Path = "$RegPath"
        Name = "ActionRequired"
        Type = "DWord"
        Value = 1
        Description = "Mark that an action is required for this cycle"
    },
    @{
        Path = "$RegPath"
        Name = "ActionCompleted"
        Type = "DWord"
        Value = 0
        Description = "Reset ActionCompleted"
    },
    @{
        Path = "$RegPath"
        Name = "ActionCompleted"
        Type = "DWord"
        Value = 0
        Description = "Reset ActionCompleted"
    }
    Get-LastBootTime
)
Apply-RegistryBatch $entries

Write-Output "[Info] PostInstall state initialized for SetupCycle $SetupCycle."

# RunActiveSetup.ps1
# Executes all Active Setup scripts in order, then initializes the PostInstall state.

$logFile = "C:\MySetup\Logs\RunActiveSetup.log"

# Logging helper
function Write-Log {
    param([string]$Message)
    $timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    "$timestamp | $Message" | Out-File $logFile -Append
}

$ActiveSetupDir = "C:\MySetup\Scripts\ActiveSetup"
$Initializer = "C:\MySetup\Scripts\EnterPostInstall.ps1"

Write-Log "[Info] Starting Active Setup execution..."

# --- 1. Run all scripts in ActiveSetup folder ---
if (Test-Path $ActiveSetupDir) {

    # Get all .ps1 files sorted alphabetically
    $Scripts = Get-ChildItem -Path $ActiveSetupDir -Filter *.ps1 -File | Sort-Object Name

    foreach ($Script in $Scripts) {
        Write-Log "[Info] Running Active Setup script: $($Script.Name)"

        try {
            & "$Script.FullName"
        }
        catch {
            Write-Log "[Warning] Active Setup script failed: $($Script.Name)"
            Write-Log "$_.Exception.Message"

            # Optional: uncomment to stop on error
            # throw
        }
    }
}
else {
    Write-Log "[Info] ActiveSetup folder not found: $ActiveSetupDir"
}


# --- 2. Run the fixed state initializer ---
if (Test-Path $Initializer) {
    Write-Log "[Info] Running EnterPostInstall.ps1..."
    try {
        & "$Initializer" >> "C:\MySetup\Logs\EnterPostInstall.log" 2>&1
    }
    catch {
        Write-Log "[Error] EnterPostInstall.ps1 failed!"
        Write-Log "$_.Exception.Message"
        return
    }
}
else {
    Write-Log "[Error] EnterPostInstall.ps1 not found: $Initializer"
    return
}

Write-Log "[Info] Active Setup execution completed."

. "C:\MySetup\Scripts\Apply-Registry.ps1"

