function Invoke-ConfigureStartPins {
    $json = '{"pinnedList":[]}'
    if ([System.Environment]::OSVersion.Version.Build -lt 20000) {
        return
    }
    $key = 'Registry::HKLM\SOFTWARE\Microsoft\PolicyManager\current\device\Start'
    New-Item -Path $key -ItemType Directory -ErrorAction SilentlyContinue | Out-Null
    Set-ItemProperty -LiteralPath $key -Name 'ConfigureStartPins' -Value $json -Type String
}
Invoke-ConfigureStartPins

function Get-WallpaperConfigurationEntries {
$entries = @()
# Detect OS build $buildNumber = [System.Environment]::OSVersion.Version.Build $wallpaperPath = $null
# Determine wallpaper based on OS version and theme if ($buildNumber -ge 22000) { $themeKey = 'HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Themes\Personalize' $lightTheme = $false
if (Test-Path $themeKey) { $value = Get-ItemProperty -Path $themeKey -Name 'SystemUsesLightTheme' -ErrorAction SilentlyContinue if ($value.SystemUsesLightTheme -eq 1) { $lightTheme = $true } }
if ($lightTheme) { $wallpaperPath = 'C:\Windows\Web\Wallpaper\Windows\img0.jpg' } else { $wallpaperPath = 'C:\Windows\Web\Wallpaper\Windows\img19.jpg' } } else { $wallpaperPath = 'C:\Windows\Web\4K\Wallpaper\Windows\img0_3840x2160.jpg' }
# If wallpaper file does not exist, return no entries if (-not (Test-Path $wallpaperPath)) { return @() # No entries produced }
# Wallpaper registry entries $entries += @( @{ Operation = "Set" Path = "HKCU:\Control Panel\Desktop" Name = "Wallpaper" Type = "String" Value = $wallpaperPath Description = "Set desktop wallpaper" }, @{ Operation = "Set" Path = "HKCU:\Control Panel\Desktop" Name = "WallpaperStyle" Type = "String" Value = "10" Description = "Set wallpaper style (fill)" }, @{ Operation = "Set" Path = "HKCU:\Control Panel\Desktop" Name = "TileWallpaper" Type = "String" Value = "0" Description = "Disable wallpaper tiling" }, @{ Operation = "Delete" Path = "HKCU:\Control Panel\Desktop" Name = "TranscodedImageCache" Description = "Remove cached wallpaper image" }, @{ Operation = "Delete" Path = "HKCU:\Control Panel\Desktop" Name = "TranscodedImageCache_000" Description = "Remove cached wallpaper image (secondary)" } )
return $entries }
