function Get-AppEventsClearCurrentEntries { param()
$eventLabelsPath = "HKCU:\AppEvents\EventLabels" $schemesRoot = "HKCU:\AppEvents\Schemes\Apps"
# Determine excluded event labels $excludes = Get-ChildItem -LiteralPath $eventLabelsPath | Where-Object { ($_ | Get-ItemProperty).ExcludeFromCPL -eq 1 } | Select-Object -ExpandProperty PSChildName
$entries = @()
Get-ChildItem -Path "$schemesRoot\*\*" | Where-Object { $_.PSChildName -notin $excludes } | Get-ChildItem -Include ".Current" | ForEach-Object { # .Name is like: HKEY_CURRENT_USER\AppEvents\Schemes\Apps\.Default\ChangeTheme\.Current $name = $_.Name $rel = $name -replace '^HKEY_CURRENT_USER\\', '' -replace '^HKEY_LOCAL_MACHINE\\', '' $path = "HKCU:\$rel"
$entries += @{ Operation = "Set" Path = $path Name = "(Default)" Type = "String" Value = "" Description = "Clear the current sound assigned to an application event" } }
return $entries }
