& {
    $formatter = {
        $args[0].ToString( "yyyy'-'MM'-'dd'T'HH':'mm':'ssK" );
    };
    $now = [datetime]::UtcNow;
    $start = & $formatter $now;
    $end = & $formatter $now.AddDays( 7.1 );

    $params = @{
        LiteralPath = 'Registry::HKLM\SOFTWARE\Microsoft\WindowsUpdate\UX\Settings';
        Type = 'String';
        Force = $true;
    };

    Set-ItemProperty @params -Name 'PauseFeatureUpdatesStartTime' -Value $start;
    Set-ItemProperty @params -Name 'PauseFeatureUpdatesEndTime' -Value $end;
    Set-ItemProperty @params -Name 'PauseQualityUpdatesStartTime' -Value $start;
    Set-ItemProperty @params -Name 'PauseQualityUpdatesEndTime' -Value $end;
    Set-ItemProperty @params -Name 'PauseUpdatesStartTime' -Value $start;
    Set-ItemProperty @params -Name 'PauseUpdatesExpiryTime' -Value $end;
} *>&1 | Out-String -Width 1KB -Stream >> "C:\MySetup\Logs\PauseWindowsUpdate.log";

# PostInstallMonitor.ps1
# Drives the PostInstall state machine by invoking PostInstallAction.ps1
# whenever ActionRequired=1 and ActionCompleted=0.

. "C:\MySetup\Scripts\Apply-Registry.ps1"

$logFile = "C:\MySetup\Logs\PostInstallMonitor.log"

# Logging helper
function Write-Log {
    param([string]$Message)
    $timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    "$timestamp | $Message" | Out-File $logFile -Append
}

$HKCU = "HKCU:\Software\MySetup\PostInstall"
$HKLM = "HKLM:\Software\MySetup\PostInstall"
$ActionScript = "C:\MySetup\Scripts\PostInstallAction.ps1"

# --- 1. Ensure state exists ---
if (-not (Test-Path $RegPath)) {
    Write-Log "[Info] PostInstall state not initialized. Exiting."
    exit 0
}

$State = Get-ItemProperty -Path $RegPath

$SetupComplete   = $State.SetupComplete
$ActionRequired  = $State.ActionRequired
$ActionCompleted = $State.ActionCompleted
$SetupCycle      = $UserState.SetupCycle

# --- 2. Read target cycle from HKLM (default = 1) ---
$TargetCycle = 1
if (Test-Path $HKLM) {
    $TargetCycle = (Get-ItemProperty -Path $HKLM -Name TargetCycle -ErrorAction SilentlyContinue).TargetCycle
    if (-not $TargetCycle) {
        Write-Log "[Warning] Defaulting TargetCycle to 1"
        $TargetCycle = 1
    }
}
else {
    Write-Log "[Warning] $HKLM not found"
}

# --- 3. If user cycle is behind - start new cycle ---
if ($SetupCycle -lt $TargetCycle) {
    Write-Log "[Info] Cycle is behind - starting new one."

    Apply-RegistryEntry -Path $HKCU -Name SetupCycle -Type "DWord" -Value $TargetCycle
    Apply-RegistryEntry -Path $HKCU -Name ActionRequired -Type "DWord" -Value 1
    Apply-RegistryEntry -Path $HKCU -Name ActionCompleted -Type "DWord" -Value 0

    # Continue into normal logic below
    $ActionRequired  = 1
    $ActionCompleted = 0
}

# --- 4. If setup not complete, exit ---
if ($SetupComplete -ne 1) {
    Write-Log "[Info] SetupComplete != 1. Exiting."
    exit 0
}

# --- 3. If no action required, exit ---
if ($ActionRequired -ne 1) {
    Write-Log "[Info] ActionRequired != 1. Exiting."
    exit 0
}

# --- 4. If action already completed, exit ---
if ($ActionCompleted -eq 1) {
    Write-Log "[Info] ActionCompleted == 1. Exiting."
    exit 0
}

# --- 5. Action still required - invoke PostInstallAction.ps1 ---
if (Test-Path $ActionScript) {
    Write-Log "[Info] Invoking PostInstallAction.ps1..."
    try {
        & $ActionScript
    }
    catch {
        Write-Log "[Warning] PostInstallAction.ps1 failed: $($_.Exception.Message)"
    }
}
else {
    Write-Log "[Warning] PostInstallAction.ps1 not found: $ActionScript"
}

exit 0
